var appName = typeof(version) == \"object\" && typeof(version.title) == \"string\" ? version.title : undefined;
var verMajor = typeof(version) == \"object\" && typeof(version.major) == \"number\" ? version.major : undefined;
console.log(appName);
console.log(verMajor);
window.client.getVersion(appName,typeof(verMajor)==\"number\");
if (appName == \"TiddlyWiki\") {
    var enableSaving = function(doc) {
        var messageBox = doc.createElement(\"div\");
        messageBox.id = \"tiddlyfox-message-box\";
        doc.body.appendChild(messageBox);
        if(appName == \"TiddlyWiki\" && typeof(verMajor) == \"number\") {
            var injectedSaveFile = function(path,content) {
                var messageBox = document.getElementById(\"tiddlyfox-message-box\");
                if(messageBox) {
                    var message = document.createElement(\"div\");
                    message.setAttribute(\"data-tiddlyfox-path\",path);
                    message.setAttribute(\"data-tiddlyfox-content\",content);
                    messageBox.appendChild(message);
                    var event = document.createEvent(\"Events\");
                    event.initEvent(\"tiddlyfox-save-file\",true,false);
                    message.dispatchEvent(event);
                }
                return true;
            };
            var injectedLoadFile = function(path) {
                if(getLocalPath(document.location.toString()) === path) {
                    return this.tiddlywikiSourceText;
                } else {
                    return false;
                }
            };

            window.mozillaSaveFile = injectedSaveFile;
            window.mozillaLoadFile = injectedLoadFile;
        }
        messageBox.addEventListener(\"tiddlyfox-save-file\",function(event) {
            var message = event.target,
            filepath = message.getAttribute(\"data-tiddlyfox-path\"),
            content = message.getAttribute(\"data-tiddlyfox-content\");
            window.client.saveWiki(filepath,content);
            message.parentNode.removeChild(message);
            var event = doc.createEvent(\"Events\");
            event.initEvent(\"tiddlyfox-have-saved-file\",true,false);
            event.savedFilePath = filepath; message.dispatchEvent(event);
            return false;
        },false);
    };
    enableSaving(document);
}

var reader = new FileReader();
reader.onload = function () {
    window.client.getB64(reader.result.replace(/^data:.+;base64,/, \'\'),\'$dest$\');
};
var blobString = \'$blob$\';
var xhr = new XMLHttpRequest();
xhr.open(\'GET\', blobString, true);
xhr.responseType = \'blob\';
xhr.onload = function(e) {
    if (this.status == 200) {
        reader.readAsDataURL(this.response);
    }
};
xhr.send();
